	private void createArray(int m, int n) {

		MazeGenerator MazeGen = new MazeGenerator(m,n);
		char[][] charMaze = MazeGen.getGrid();
		int[][] tempMaze = new int[charMaze[0].length][charMaze.length];
		
		for(int x=0; x < charMaze[0].length; x++) {
			for(int y = 0; y < charMaze.length; y++) {
				if(charMaze[y][x]!=' ') tempMaze[x][y]=100;
			}
		}
		
		int[][] newTemp = skinnify(tempMaze);
		
		this.maze =  newTemp;
	}
	
	
